import React from 'react';
import { Link } from 'react-router-dom';
import { getProductsFromCategoryAndQuery } from '../services/api';
import Card from '../components/Card';
import Categories from '../components/Categories';
import Header from '../components/Header';

class Home extends React.Component {
  constructor() {
    super();
    this.state = {
      text: '',
      categoryId: '',
      results: [],
      didSearch: false,
      cartItems: [],
    };
  }

  componentDidMount() {
    this.setCart();
  }

  componentDidUpdate() {
    this.addToLocalCart();
  }

  componentWillUnmount() {
    const { results } = this.state;
    localStorage.setItem('results', JSON.stringify(results));
  }

  setCart = () => {
    const cartItemsLocal = JSON.parse(localStorage.getItem('carrinho'));
    if (Array.isArray(cartItemsLocal)) {
      this.setState({ cartItems: cartItemsLocal });
    }
  }

  addToLocalCart = () => {
    const { cartItens } = this.state;
    localStorage.setItem('carrinho', JSON.stringify(cartItens));
  }

  handleClick = () => {
    const { categoryId, text } = this.state;
    getProductsFromCategoryAndQuery(categoryId, text).then((response) => {
      this.setState({
        didSearch: true,
        results: response.results,
      });
    });
  };

  addToCart = ({ target }) => {
    const { id } = target;
    const { results, cartItens } = this.state;
    const objProduct = {
      title: results[id].title,
      thumbnail: results[id].thumbnail,
      price: results[id].price,
      id: results[id].id,
    };
    if (!cartItens.some((item) => item.title === objProduct.title)) {
      this.setState((prevState) => ({
        cartItens: [...prevState.cartItens, objProduct],
      }));
    }
  }

  categoryChecked = async ({ target }) => {
    const { text } = this.state;
    this.setState({ categoryId: target.id });

    const response = await getProductsFromCategoryAndQuery(target.id, text);
    this.setState({ results: response.results, didSearch: true });
  }

  onInputChange = ({ target }) => {
    const { name } = target;
    const formElementValue = target.type === 'checkbox' ? target.checked : target.value;

    this.setState({ [name]: formElementValue });
  }

  render() {
    const { results, didSearch, text, cartItens } = this.state;

    return (
      <div>
        <Header cartItems={cartItens}/>
        <p>{`Quantidade de Produtos no carrinho:${cartItens.length}`}</p>
        <Link data-testid="shopping-cart-button" to="/cart">Carrinho</Link>
        <label htmlFor="search">
          <input
            data-testid="query-input"
            name="text"
            id="search"
            value={ text }
            onChange={ this.onInputChange }
          />
        </label>
        <button
          type="button"
          onClick={ this.handleClick }
          data-testid="query-button"
        >
          pesquisar
        </button>
        { !didSearch
          ? (
            <p data-testid="home-initial-message">
              Digite algum termo de pesquisa ou escolha uma categoria.
            </p>)
          : results.map((item, i) => (
            <div key={ item.id } data-testid="product">
              <Card
                id={ item.id }
                title={ item.title }
                thumbnail={ item.thumbnail }
                price={ item.price }
                idItem={ i }
                addToCart={ this.addToCart }
              />
            </div>
          ))}
        { results.length !== 0 && <p>Nenhum produto foi encontrado</p> }
        <aside>
          <Categories categoryChecked={ this.categoryChecked } />
        </aside>
      </div>
    );
  }
}

export default Home;
