import React from 'react';
import { Link } from 'react-router-dom';
import '../styles/Header.css';

class Header extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      totalItemsOnCart: 0,
      purchaseTotalValue: 0
    }
  }

  componentDidMount() {

  }

  getContentFromLocSt = () => {
    const totalItemsOnCart = JSON.parse(localStorage.getItem("totalItemsOnCart"));
    const purchaseTotalValue = JSON.parse(localStorage.getItem("purchaseTotalValue"));

    if (totalItemsOnCart !== undefined) {
      this.setState({
        totalItemsOnCart,
        purchaseTotalValue
      });
    }
  }

  render() {
    const { loading } = this.props;


    return (
      <header data-testid="header-component">
        <div id="logoAndUserBar">
          {/* <img src={logo} alt="trybetunes-logo" /> */}
          <h1>Logo</h1>
        </div>
        <div id="linksBar">
          <Link to="/">Pesquisa</Link>
          <Link to="/cart">{`Carrinho - ${totalItemsOnCart} produtos - R$ ${purchaseTotalValue.toFixed(2)}`}</Link>
        </div>
        { loading &&
          <div id="loadingContainer">
            <span>Carregando...</span>
          </div>
        }
      </header>
    );
  }
}

export default Header;
