import React from 'react';
import { getProductsFromCategoryAndQuery } from '../services/api';
import Card from '../components/Card';
import Categories from '../components/Categories';
import Header from '../components/Header';

class Home extends React.Component {
  constructor() {
    super();
    this.state = {
      loading: false,
      userSearchedItem: '',
      categoryId: '',
      results: [],
      didSearch: false,
      cartItems: [],
    };
  }

  componentDidMount() {
    this.getLocStUserCart();
  }

  componentDidUpdate() {
    this.setLocStUserCart();
  }

  componentWillUnmount() {
    const { results } = this.state;
    localStorage.setItem('results', JSON.stringify(results));
  }

  handleClick = async () => { // Fun칞칚o que realiza a requisi칞칚o para API getProductsFromCategoryAndQuery(), baseada no termo pesquisado. Ser치 chamada no onClick do bot칚o de pesquisar.
    this.setState({ loading: true });
    const { categoryId, userSearchedItem } = this.state;

    const response = await getProductsFromCategoryAndQuery(categoryId, userSearchedItem);

    this.setState({
      didSearch: true,
      results: response.results,
      loading: false,
    });
  }

  onInputChange = ({ target }) => { // Fun칞칚o que altera o estado 'userSearchedItem', no momento que o usu치rio realiza uma busca. Ser치 chamada no onChange de #searchBar.
    const { name } = target;
    const formElementValue = target.type === 'checkbox' ? target.checked : target.value;

    this.setState({ [name]: formElementValue });
  }

  categoryChecked = async ({ target }) => { // Fun칞칚o que realiza a requisi칞칚o para API getProductsFromCategoryAndQuery(), baseada na categoria clicada. Ser치 chamada no onChange de cada radio, de cada categoria.
    const { userSearchedItem } = this.state;
    this.setState({ loading: true, categoryId: target.id });

    const response = await getProductsFromCategoryAndQuery(target.id, userSearchedItem);
    this.setState({ results: response.results, didSearch: true, loading: false });
  }

  getLocStUserCart = () => { // Fun칞칚o que capta os itens do carrinho salvos no local storage, sempre que a p치gina Home for montada. Tal fun칞칚o evita que o carrinho do usu치rio seja restaurado.
    const cartItemsFromLocSt = JSON.parse(localStorage.getItem('userCart'));

    if (Array.isArray(cartItemsFromLocSt)) { // Caso a key "userCart", de local storage, N츾O seja um array vazio...
      this.setState({ cartItems: cartItemsFromLocSt });
    }
  }

  setLocStUserCart = () => { // Fun칞칚o que atualiza a key "userCart", de local storage, sempre que algum estado da p치gina Home for atualizado [componentDidUpdate()].
    const { cartItems } = this.state;
    localStorage.setItem('userCart', JSON.stringify(cartItems));
  }

  addToCart = ({ target }) => { // Fun칞칚o que permite a adi칞칚o de um determinado produo
    const { id } = target;
    const { results, cartItems } = this.state;
    const objProduct = {
      title: results[id].title,
      thumbnail: results[id].thumbnail,
      price: results[id].price,
      id: results[id].id,
    };

    if (!cartItems.some((item) => item.title === objProduct.title)) {
      this.setState((prevState) => ({
        cartItems: [...prevState.cartItems, objProduct],
      }));
    }
  }

  render() {
    const { loading, results, didSearch, userSearchedItem, cartItems } = this.state;

    return (
      <div id="homepage">
        <Header loading={loading} cartItems={cartItems}/>

        <section id="homepageCenter">
          <div id="searchContainer">
            <label htmlFor="search">
              <input
                id="searchBar"
                data-testid="query-input"
                name="userSearchedItem"
                value={ userSearchedItem }
                onChange={ this.onInputChange }
              />
            </label>
            <button
              type="button"
              onClick={ this.handleClick }
              data-testid="query-button"
            >
              <span role="img" aria-label="emoji-lupa">游댍</span>
            </button>
          </div>
          <div id="productsDisplay">
            { !didSearch
              ? (
                <p data-testid="home-initial-message">
                  Busque um produto ou escolha uma categoria!
                </p>)
              : results.map((item, index) => (
                <div key={ item.id } data-testid="product">
                  <Card
                    id={ item.id }
                    title={ item.title }
                    thumbnail={ item.thumbnail }
                    price={ item.price }
                    idItem={ index }
                    addToCart={ this.addToCart }
                  />
                </div>
              ))}
          </div>
          <aside id="categoriesContainer1">
            <Categories categoryChecked={ this.categoryChecked } />
          </aside>
        </section>
      </div>
    );
  }
}

export default Home;
