import React from 'react';
import PropTypes from 'prop-types';

class CardsInCart extends React.Component {
  constructor(props) {
    super(props);

    const { price } = this.props;
    this.state = {
      quantity: 1,
      price,
    };
  }

  componentDidMount() {
    const { attPrices } = this.props;
    const { price } = this.state;
    attPrices(price);
  }

  componentWillUnmount() {
    const { subPrices } = this.props;
    const { price } = this.state;
    subPrices(price);
  }

  addItem = () => {
    const { attPrices } = this.props;
    const { price } = this.state;
    this.setState((prevValue) => ({
      quantity: prevValue.quantity + 1,
    }));
    attPrices(price);
  }

  subItem = () => {
    const { subPrices } = this.props;
    const { quantity, price } = this.state;
    if (quantity > 1) {
      this.setState((prevValue) => ({
        quantity: prevValue.quantity - 1,
      }));
      subPrices(price);
    }
  }

  render() {
    const { thumbnail, title, price, id, deleteCartItem } = this.props;
    const { quantity } = this.state;

    return (
      <div data-testid="shopping-cart-product-name">
        <h3>{title}</h3>
        <img src={ thumbnail } alt={ title } />
        <p>{ price.toFixed(2) }</p>
        <button
          id={ id }
          type="button"
          onClick={ deleteCartItem }
        >
          X
        </button>
        <button
          type="button"
          onClick={ this.subItem }
          id={ id }
          data-testid="product-decrease-quantity"
        >
          -
        </button>
        <output
          name="quantity"
          data-testid="shopping-cart-product-quantity"
        >
          {quantity}
        </output>
        <button
          type="button"
          onClick={ this.addItem }
          data-testid="product-increase-quantity"
        >
          +
        </button>
      </div>
    );
  }
}

CardsInCart.propTypes = {
  price: PropTypes.number.isRequired,
  attPrices: PropTypes.func.isRequired,
  subPrices: PropTypes.func.isRequired,
  thumbnail: PropTypes.string.isRequired,
  title: PropTypes.string.isRequired,
  id: PropTypes.string.isRequired,
  deleteCartItem: PropTypes.func.isRequired,
};

export default CardsInCart;
