import React from 'react';
import { Link } from 'react-router-dom';
import '../styles/Header.css';

class Header extends React.Component {
  constructor(props) {
    super(props);
  }

  numOfCartItems = () => { // Função que capta o número de itens, atualmente, dispostos no carrinho.
    const { cartItemsFromHome } = this.props;
    let numberOfCartItems = 0;

    if (cartItemsFromHome.length > 0) {
      const quantitiesArray = cartItemsFromHome.map((microObj) => microObj.quantity);
      const totalQuant = quantitiesArray.reduce((result, value) => result + value);
      numberOfCartItems = totalQuant;
    }

    return numberOfCartItems;
  }

  cartTotalValue = () => { // Função que capta o número de itens, atualmente, dispostos no carrinho.
    const { cartItemsFromHome } = this.props;
    let totalValue = 0;

    if (cartItemsFromHome.length > 0) {
      const totalValuesArray = cartItemsFromHome.map((microObj) => microObj.totalValue);
      const totalValue = totalValuesArray.reduce((result, value) => result + value);
      numberOfCartItems = totalQuant;
    }

    return numberOfCartItems;
  }

  render() {
    const { loading, cartItemsFromHome } = this.props;
    // const totalItemsOnCart = JSON.parse(localStorage.getItem("totalItemsOnCart"));
    // const purchaseTotalValue = JSON.parse(localStorage.getItem("purchaseTotalValue"));

    return (
      <header data-testid="header-component">
        <div id="logoAndUserBar">
          {/* <img src={logo} alt="trybetunes-logo" /> */}
          <h1>Logo</h1>
        </div>
        <div id="linksBar">
          <Link to="/">Pesquisa</Link>
          <Link to="/cart">{`Carrinho - ${this.numOfCartItems()} produto(s) - R$ `} </Link>
        </div>
        { loading &&
          <div id="loadingContainer">
            <span>Carregando...</span>
          </div>
        }
      </header>
    );
  }
}

export default Header;
