import React from 'react';
import { Link } from 'react-router-dom';
import '../styles/Header.css';

class Header extends React.Component {
  constructor(props) {
    super(props);

    const { cartItemsFromHome } = this.props;
    this.state = {
      totalItemsOnCart: 0,
      purchaseTotalValue,
      cartItemsFromHome,
    }
  }

  componentDidUpdate() {
    this.jf();
    console.log('joão')
  }

  jf = () => {
    const totalItemsOnCart = JSON.parse(localStorage.getItem("totalItemsOnCart"));
    return totalItemsOnCart;
  }

  componentWillMount() {
    this.settingLocalStorage();
    this.settingState();
  }

  settingLocalStorage = () => {
    const totalItemsOnCart = JSON.parse(localStorage.getItem("totalItemsOnCart"));
    // const purchaseTotalValue = JSON.parse(localStorage.getItem("purchaseTotalValue"));

    if (totalItemsOnCart === null) {
      localStorage.setItem('userCart', JSON.stringify([]));
      localStorage.setItem('totalItemsOnCart', JSON.stringify(0));
      localStorage.setItem('purchaseTotalValue', JSON.stringify(0));
    } 
  }

  settingState = () => {
    const totalItemsOnCart = JSON.parse(localStorage.getItem("totalItemsOnCart"));
    const purchaseTotalValue = JSON.parse(localStorage.getItem("purchaseTotalValue"));

    this.setState({
      totalItemsOnCart,
      purchaseTotalValue,
    });
  }

  render() {
    const { loading } = this.props;
    const { totalItemsOnCart, purchaseTotalValue } = this.state;

    return (
      <header data-testid="header-component">
        <div id="logoAndUserBar">
          {/* <img src={logo} alt="trybetunes-logo" /> */}
          <h1>Logo</h1>
        </div>
        <div id="linksBar">
          <Link to="/">Pesquisa</Link>
          <Link to="/cart">{`Carrinho - ${this.jf()} produto(s) - R$ ${purchaseTotalValue.toFixed(2)}`}</Link>
        </div>
        { loading &&
          <div id="loadingContainer">
            <span>Carregando...</span>
          </div>
        }
      </header>
    );
  }
}

export default Header;
