import React from 'react';
import { Link } from 'react-router-dom';
import '../styles/Header.css';

class Header extends React.Component {
  constructor(props) {
    super(props);

    const { cartItemsFromHome } = this.props
    this.state = {
      totalItemsOnCart: 0,
      purchaseTotalValue: 0,
      cartItemsFromHome,
    }
  }

  componentDidMount() {
    localStorage.setItem("totalItemsOnCart", 0);
    localStorage.setItem("purchaseTotalValue", 0);
  }

  render() {
    const { loading } = this.props;
    const { totalItemsOnCart, purchaseTotalValue } = this.state;
    const totalItemsOnCart1 = JSON.parse(localStorage.getItem("totalItemsOnCart"));
    const purchaseTotalValue1 = JSON.parse(localStorage.getItem("purchaseTotalValue"));

    return (
      <header data-testid="header-component">
        <div id="logoAndUserBar">
          {/* <img src={logo} alt="trybetunes-logo" /> */}
          <h1>Logo</h1>
        </div>
        <div id="linksBar">
          <Link to="/">Pesquisa</Link>
          <Link to="/cart">{`Carrinho - ${totalItemsOnCart1} produto(s) - R$ ${purchaseTotalValue1.toFixed(2)}`}</Link>
        </div>
        { loading &&
          <div id="loadingContainer">
            <span>Carregando...</span>
          </div>
        }
      </header>
    );
  }
}

export default Header;
