import React from 'react';
import PropTypes from 'prop-types';

class CartCard extends React.Component {
  constructor(props) {
    super(props);

    const { productPrice } = this.props;
    this.state = {
      productQuant: 1,
      productPrice,
    };
  }

  componentDidMount() { // Explicação na função purchaseTVIncrease(), presente no componente Cart.
    const { purchaseTVIncrease } = this.props;
    const { productPrice } = this.state;
    purchaseTVIncrease(productPrice);
  }

  componentWillUnmount() { // Explicação na função purchaseTVDecrease(), presente no componente Cart.
    const { purchaseTVDecrease } = this.props;
    const { productPrice } = this.state;
    purchaseTVDecrease(productPrice);
  }

  // addItem = () => { // Função responsável por AUMENTAR, em uma unidade, o número mostrado no <output> contido entre "-" e "+". Além disso, chama-se a função purchaseTVIncrease() para que o valor total da compra também aumente.
  //   const { purchaseTVIncrease } = this.props;
  //   const { productPrice } = this.state;

  //   this.setState((prevState) => ({
  //     productQuant: prevState.productQuant + 1,
  //   }));
  //   purchaseTVIncrease(productPrice);
  // }

  addItem = ({ target }) => {
    const userCartFromLocSt = JSON.parse(localStorage.getItem("userCart"));

    userCartFromLocSt.forEach((microObj) => {
      if (microObj.productId === target.id) {
        microObj.quantity += 1
      }
    });

    const newQuantity = userCartFromLocSt.find((microObj) => microObj.productId === target.id).quantity

    this.setState({  })
    localStorage.setItem("userCart", JSON.stringify(userCartFromLocSt));
  }

  subItem = () => { // Função responsável por DIMINUIR, em uma unidade, o número mostrado no <output> contido entre "-" e "+". Além disso, chama-se a função purchaseTVDecrease() para que o valor total da compra também reduza.
    const { purchaseTVDecrease } = this.props;
    const { productQuant, productPrice } = this.state;

    if (productQuant > 1) { // A respectiva condicional evita que o <output> contido entre "-" e "+" mostre um número menor que 1.
      this.setState((prevState) => ({
        productQuant: prevState.productQuant - 1,
      }));
      purchaseTVDecrease(productPrice);
    }
  }

  render() {
    const { thumbnail, title, productPrice, productId, deleteCartItem } = this.props;
    const { productQuant } = this.state;

    return (
      <div className="eachCartCard" data-testid="shopping-cart-product-name">
        <h3>{ title }</h3>
        <img src={ thumbnail } alt={ title } width="170px" />
        <p>{ `R$ ${ productPrice.toFixed(2) }` }</p>
        <button
          id={ productId }
          type="button"
          onClick={ deleteCartItem }
        >
          X
        </button>
        <button
          id={ productId }
          type="button"
          onClick={ this.subItem }
          data-testid="product-decrease-quantity"
        >
          -
        </button>
        <output
          data-testid="shopping-cart-product-quantity"
        >
          {productQuant}
        </output>
        <button
          id={ productId }
          type="button"
          onClick={ this.addItem }
          data-testid="product-increase-quantity"
        >
          +
        </button>
      </div>
    );
  }
}

CartCard.propTypes = {
  productPrice: PropTypes.number.isRequired,
  purchaseTVIncrease: PropTypes.func.isRequired,
  purchaseTVDecrease: PropTypes.func.isRequired,
  thumbnail: PropTypes.string.isRequired,
  title: PropTypes.string.isRequired,
  productId: PropTypes.string.isRequired,
  deleteCartItem: PropTypes.func.isRequired,
};

export default CartCard;
