import React from 'react';
import CardsInCart from '../components/CardsInCart';

class Cart extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      cartItems: [],
      total: 0,
    };
  }

  componentDidMount() {
    this.insertItensInTheCart();
  }

  componentDidUpdate() {
    this.attLocalStorage();
  }

  insertItensInTheCart = () => { // 
    const cartItems = JSON.parse(localStorage.getItem('userCart'));

    if (Array.isArray(cartItems)) {
      this.setState({ cartItems });
    }
  }

  attLocalStorage = () => {
    const { cartItems } = this.state;
    localStorage.setItem('userCart', JSON.stringify(cartItems));
  }

  deleteCartItem = ({ target }) => {
    const { cartItems } = this.state;
    const findIndex = cartItems.findIndex((index) => index.id === target.id);
    console.log(findIndex);
    this.setState({
      cartItems: cartItems.filter((_item, i) => i !== findIndex),
    });
  }

  attPrices = (price) => {
    this.setState((prevState) => ({
      total: prevState.total + price,
    }));
  }

  subPrices = (price) => {
    this.setState((prevState) => ({
      total: prevState.total - price,
    }));
  }

  render() {
    const { cartItems, total } = this.state;
    return (
      <div>
        <p>{cartItems.length}</p>
        {cartItems.length === 0
          ? <span data-testid="shopping-cart-empty-message">Seu carrinho está vazio</span>
          : cartItems.map((cartItem) => (
            <div key={ cartItem.title }>
              <CardsInCart
                title={ cartItem.title }
                thumbnail={ cartItem.thumbnail }
                price={ cartItem.price }
                id={ cartItem.id }
                deleteCartItem={ this.deleteCartItem }
                attPrices={ this.attPrices }
                subPrices={ this.subPrices }
              />
            </div>
          ))}
        <h3>{total.toFixed(2)}</h3>
      </div>
    );
  }
}

export default Cart;
