import React from 'react';
import PropTypes from 'prop-types';

class Review extends React.Component {
  constructor(props) {
    super(props);
    const { productId } = this.props;
    const reviews = JSON.parse(localStorage.getItem(productId));

    if (reviews === null) {

    }

    this.state = {
      name: '',
      comment: '',
      reviewStar: '',
      reviews: [],
    };
  }

  componentDidMount() {
    this.setReview();
  }


  setReview = () => {
    const { productId } = this.props;
    const reviews = JSON.parse(localStorage.getItem(productId));
    if (Array.isArray(reviews)) {
      this.setState({ reviews });
    }
  }

  handleSubmit = (event) => { // Função que evita que o forms seja POST, quando o usuário apertar o botão 'Avaliar'. É chamada dentro do onSubmit, do #reviewForm.
    event.preventDefault();
  }

  handleChange = ({ target }) => { // Função que altera o estado 'name', 'comment' e/ou 'reviewStar' no momento que o usuário realiza alguma modificação. Será chamada nos onChanges de #reviewForm.
    const { name, value } = target;

    this.setState({
      [name]: value,
    });
  }

  sendReview = () => { // Função que altera o estado cuja key é igual à 'reviews' e salva, no local storage, a avaliação do usuário.
    const { comment, reviewStar, name } = this.state;
    const { productId } = this.props;
    const objComment = {
      comment,
      reviewStar,
      name,
    };

    this.setState((prevState) => ({
      reviews: [...prevState.reviews, objComment],
      comment: '',
      name: '',
    }), () => localStorage.setItem(productId, JSON.stringify(this.state.reviews)));
  }

  render() {
    const { name, comment, reviews } = this.state;

    return (
      <div id="reviewFormContainer">
        <form id="reviewForm" onSubmit={ this.handleSubmit }>
          <label htmlFor="name">
            <input
              id="name"
              type="text"
              name="name"
              placeholder="Nome"
              value={ name }
              onChange={ this.handleChange }
              required
            />
          </label>
          <br />

          <label htmlFor="1">
            1
            <input
              id="1"
              type="radio"
              name="reviewStar"
              value="1"
              onClick={ this.handleChange }
              required
            />
          </label>
          <label htmlFor="2">
            2
            <input
              id="2"
              type="radio"
              name="reviewStar"
              value="2"
              onClick={ this.handleChange }
              required
            />
          </label>
          <label htmlFor="3">
            3
            <input
              id="3"
              type="radio"
              name="reviewStar"
              value="3"
              onClick={ this.handleChange }
              required
            />
          </label>
          <label htmlFor="4">
            4
            <input
              id="4"
              type="radio"
              name="reviewStar"
              value="4"
              onClick={ this.handleChange }
              required
            />
          </label>
          <label htmlFor="5">
            5
            <input
              id="5"
              type="radio"
              name="reviewStar"
              value="5"
              onClick={ this.handleChange }
              required
            />
          </label>
          <br />

          <textarea
            data-testid="product-detail-evaluation"
            rows="8"
            cols="40"
            placeholder="Mensagem (opcional)"
            onChange={ this.handleChange }
            name="comment"
            value={ comment }
          />
          <br />

          <button type="submit" onClick={ this.sendReview }>Avaliar</button>
        </form>
        {reviews.length > 0
        && reviews.map((review) => (
          <div key={ review.comment }>
            <h2>{ review.name }</h2>
            <h3>{ `Nota: ${review.reviewStar}` }</h3>
            <p>{ review.comment }</p>
          </div>
        ))}
      </div>
    );
  }
}

Review.propTypes = {
  productId: PropTypes.string.isRequired,
};

export default Review;
